import React, {useState} from 'react';
import restaurants from "../sample-restaurants";
import PropTypes from 'prop-types';

const Landing = (props) => {

    const state = {
        display: false,
        title: '',
        url: ''
    };

    const [display, toggleDisplay] = useState(false);

    const displayList = () => {
        toggleDisplay(!display);
    };

    const getTitle = (restauran) => {
        const {url, title} = restauran;
        this.setState({url, title, display:false})
    }

    const goToRestaurant = () => {
        const{url} = state;
        props.history.push(`/restaurant/${url}`)
    }

    return (
        <div className='restaurant-select'>
            <div className='restaurant_select-top'>
                <div onClick={displayList} className='restaurant_select_top-header font-effect-outline'>
                    {state.title ? state.title : 'Оберіть ресторан'}
                </div>
                <div className='arrow_picker'>
                    <div className='arrow_picker-up'></div>
                    <div className='arrow_picker-down'></div>
                </div>
            </div>

            {state.display ? (
                    <div className='restaurant_select_bottom'>
                        <ul>
                            {restaurants.map(restauran => {
                                return <li onClick={() => getTitle(restauran)} key={restauran.id}>{restauran.title}</li>;
                            })}
                        </ul>
                    </div>)
                : null}

            {state.title ? <button onClick={goToRestaurant}>Перейти в ресторан</button> : null}
        </div>
    );
}
