import React from 'react';
import restaurants from "../sample-restaurants";

export default class Landing extends React.Component {
    render() {
        let state = {
            display: false,
            title: '',
            url: ''
        };

        const displayList = () => {
            const display = this.state.display;
            this.setState({display:!display});
        };


        return (
            <div className='restaurant-select'>
                <div className='restaurant_select-top'>
                    <div onClick={this.displayList} className='restaurant_select_top-header'>Оберіть ресторан</div>
                    <div className='arrow_picker'>
                        <div className='arrow_picker-up'></div>
                        <div className='arrow_picker-down'></div>
                    </div>
                </div>

                {state.display ? (
                    <div className='restaurant_select_bottom'>
                        <ul>
                            {restaurants.map(restauran => {
                                return <li key={restauran.id}>{restauran.title}</li>;
                            })}
                        </ul>
                    </div>)
                    : null}

                <button>Перейти в ресторан</button>
            </div>
        );
    }
}
