import React from 'react';
import restaurants from "../sample-restaurants";

export default class Landing extends React.Component {
    state = {
        display: false,
        title: '',
        url: ''
    };


    displayList = () => {
        const display = this.state.display;
        this.setState({display:!display});
    };

    getTitle = (restauran) => {
        const {url, title} = restauran;
        this.setState({url, title, display:false})
    }

    goToRestaurant = () => {
        const{url} = this.state;
        this.props.history.push(`/restaurant/${url}`)
    }

    render() {
        return (
            <div className='restaurant-select'>
                <div className='restaurant_select-top'>
                    <div onClick={this.displayList} className='restaurant_select_top-header font-effect-outline'>
                        {this.state.title ? this.state.title : 'Оберіть ресторан'}
                    </div>
                    <div className='arrow_picker'>
                        <div className='arrow_picker-up'></div>
                        <div className='arrow_picker-down'></div>
                    </div>
                </div>

                {this.state.display ? (
                    <div className='restaurant_select_bottom'>
                        <ul>
                            {restaurants.map(restauran => {
                                return <li onClick={() => this.getTitle(restauran)} key={restauran.id}>{restauran.title}</li>;
                            })}
                        </ul>
                    </div>)
                    : null}

                {this.state.title ? <button onClick={this.goToRestaurant}>Перейти в ресторан</button> : null}
            </div>
        );
    }
}
