import React from "react";
import Header from "./Header";
import Order from "./Order";
import MenuAdmin from "./MenuAdmin";
import sampleBurgers from "../sample-burgers";
import Burger from "./Burger";
import base from "../base";

class App extends React.Component {

    state = {
        burgers: {},
        order: {}
    }

    componentDidMount() {
        this.ref = base.syncState()
    }

    addBurger = (burger) => {
        const burgers = {...this.state.burgers};

        burgers[`burger${Date.now()}`] = burger;

        this.setState({ burgers });

        console.log(this.state);
    }

    addToOrder = key => {
        const order = {...this.state.order}

        order[key] = order[key] + 1 || 1;

        this.setState({ order : order });
        /*const newOrder = order;
        this.loadSampleOrders(newOrder);*/
    }

    /*loadSampleOrders = (newOrder) => {
        this.setState({order : newOrder})
    }*/

    loadSampleBurgers = () => {
        this.setState({burgers: sampleBurgers});
    }

    render() {
        return(
            <div className='burger-paradise'>
                <div className='menu'>
                    <Header title='Very Hot Burgers' />
                    <ul>
                        {Object.keys(this.state.burgers).map(key => <Burger
                            key={key}
                            index={key}
                            addToOrder={this.addToOrder}
                            details={this.state.burgers[key]}
                        />)}
                    </ul>
                </div>
                    <Order burgers={this.state.burgers} order={this.state.order}/>
                    <MenuAdmin loadSampleBurgers={this.loadSampleBurgers} addBurger={this.addBurger}/>
            </div>
        )
    }
}

export default App;
