import React from "react";
import PropTypes from "prop-types";

class EditBurgerForm extends React.Component {

    static propTypes = {
        burger: PropTypes.shape({
            name: PropTypes.string,
            image: PropTypes.string,
            price: PropTypes.number,
            desc: PropTypes.string,
            status: PropTypes.string
        }),
        index: PropTypes.string,
        updateBurgers: PropTypes.func,
        deleteBurger: PropTypes.func

    }

    handleChange  = event => {
        const updatedBurger = {
            ...this.props.burger,
            [event.currentTarget.name]: 
                event.currentTarget.value
        };

        this.props.updateBurgers(this.props.index, updatedBurger);
    }
    render() {
        return (

            <div className='burger-edit'>
                <input onChange={this.handleChange} name='name' type='text' value={this.props.burger.name}/>
                <input onChange={this.handleChange} name='price' type='text' value={this.props.burger.price}/>
                <select onChange={this.handleChange} className='status' name='status' value={this.props.burger.status}>
                    <option value='available'>Доступно</option>
                    <option value='available'>Недоступно</option>
                </select>
                <textarea onChange={this.handleChange} name='desc' value={this.props.burger.desc}/>
                <input onChange={this.handleChange} name='image' type='text' value={this.props.burger.image}/>
                <button onClick={() => this.props.deleteBurger(this.props.index)}>Видалити бургер</button>

            </div>
        )
    }
}

export default EditBurgerForm;
